import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import Filter from './Filter';
import { filterData, apirUrl } from './data';
import Cards from './Cards';
import Spinner from './Spinner';
import { toast } from 'react-toastify';


function App() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState(filterData[0].title);
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch the courses and store them in courses array
  async function fetchData() {
    try {
      setLoading(true);
      let response = await fetch(apirUrl);
      let data = await response.json();
      setCourses(data.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Error fetching data');
    }
    setLoading(false);
  }

  // Run on first render
  useEffect(() => {
    fetchData();
  }, []);

  // Handle search based on the query
  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  // Filter courses based on search query
  const filteredCourses = courses.filter((course) =>
    course.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
      <Navbar />
      <div className='py-2 bg-gray-800' style={{ position: 'sticky', top: 0, zIndex: 2 }}>
        <Filter filterData={filterData} category={category} setCategory={setCategory} />
      </div>
      <div>
        {loading ? <Spinner /> : <Cards courses={courses} category={category} />}
      </div>
    </>
  );
}

export default App;
